```gdscript
# 方法3：解析.tscn文件结构
func parse_tscn_content(content: String) -> Dictionary:
	var result = {
		"header": {},
		"ext_resources": [],
		"nodes": [],
		"connections": []
	}

	var lines = content.split("\n")
	var current_section = ""

	for line in lines:
		line = line.strip_edges()
		if line.begins_with("[gd_scene"):
			# 解析头部信息
			result["header"] = parse_header(line)
			current_section = "header"
		elif line.begins_with("[ext_resource"):
			# 解析外部资源
			result["ext_resources"].append(parse_ext_resource(line))
			current_section = "ext_resource"
		elif line.begins_with("[node"):
			# 解析节点
			result["nodes"].append(parse_node_header(line))
			current_section = "node"
		elif line.begins_with("[connection"):
			# 解析连接
			result["connections"].append(parse_connection(line))
			current_section = "connection"
		elif line != "" and current_section == "node":
			# 解析节点属性
			parse_node_property(line, result["nodes"][-1])

	return result

func parse_header(line: String) -> Dictionary:
	var header = {}
	# 示例: [gd_scene load_steps=2 format=3 uid="uid://"]
	var parts = line.trim_prefix("[gd_scene ").trim_suffix("]").split(" ")
	for part in parts:
		var key_value = part.split("=")
		if key_value.size() == 2:
			header[key_value[0]] = key_value[1].trim_prefix('"').trim_suffix('"')
	return header

func parse_ext_resource(line: String) -> Dictionary:
	var resource = {}
	# 示例: [ext_resource type="Script" path="res://test/test.gd" id="1_4q8jf"]
	var parts = line.trim_prefix("[ext_resource ").trim_suffix("]").split(" ")
	for part in parts:
		var key_value = part.split("=")
		if key_value.size() == 2:
			resource[key_value[0]] = key_value[1].trim_prefix('"').trim_suffix('"')
	return resource

func parse_node_header(line: String) -> Dictionary:
	var node = {"properties": {}}
	# 示例: [node name="测试" type="Node2D"]
	var parts = line.trim_prefix("[node ").trim_suffix("]").split(" ")
	for part in parts:
		var key_value = part.split("=")
		if key_value.size() == 2:
			node[key_value[0]] = key_value[1].trim_prefix('"').trim_suffix('"')
	return node

func parse_node_property(line: String, node: Dictionary):
	# 示例: position = Vector2(0, 0)
	var parts = line.split("=")
	if parts.size() == 2:
		var key = parts[0].strip_edges()
		var value = parts[1].strip_edges()
		node["properties"][key] = value

func parse_connection(line: String) -> Dictionary:
	var connection = {}
	# 示例: [connection signal="pressed" from="Button" to="." method="_on_button_pressed"]
	var parts = line.trim_prefix("[connection ").trim_suffix("]").split(" ")
	for part in parts:
		var key_value = part.split("=")
		if key_value.size() == 2:
			connection[key_value[0]] = key_value[1].trim_prefix('"').trim_suffix('"')
	return connection
```

[font_size=16][b]四、使用SceneState获取详细信息[/b][/font_size]

```gdscript
# 方法4：使用SceneState获取场景详细信息
func get_scene_details(packed_scene: PackedScene) -> Dictionary:
	var details = {
		"node_count": 0,
		"nodes": [],
		"resources": [],
		"connections": []
	}

	var scene_state = packed_scene.get_state()
	details["node_count"] = scene_state.get_node_count()

	# 获取所有节点信息
	for i in range(scene_state.get_node_count()):
		var node_info = {
			"path": scene_state.get_node_path(i),
			"type": scene_state.get_node_type(i),
			"name": scene_state.get_node_name(i),
			"instance": scene_state.get_node_instance(i),
			"owner": scene_state.get_node_owner(i),
			"groups": scene_state.get_node_groups(i),
			"properties": []
		}

		# 获取节点属性
		for j in range(scene_state.get_node_property_count(i)):
			var prop_name = scene_state.get_node_property_name(i, j)
			var prop_value = scene_state.get_node_property_value(i, j)
			node_info["properties"].append({
				"name": prop_name,
				"value": prop_value
			})

		details["nodes"].append(node_info)

	# 获取连接信息
	for i in range(scene_state.get_connection_count()):
		var conn_info = {
			"signal": scene_state.get_connection_signal(i),
			"from": scene_state.get_connection_source(i),
			"to": scene_state.get_connection_target(i),
			"method": scene_state.get_connection_method(i),
			"flags": scene_state.get_connection_flags(i),
			"binds": scene_state.get_connection_binds(i),
			"unbinds": scene_state.get_connection_unbinds(i)
		}
		details["connections"].append(conn_info)

	return details
```

